import React, { FC, ChangeEvent, useState } from 'react';
import './App.css';
import { ITask } from './Interfaces/ITask';

  const App: FC = () =>  {
    const [title, setTitle] = useState<string>("");
    const [description, setDescription] = useState<string>("");
    const [todoList, setTodoList] = useState<ITask[]>([]);

    const handleChange = (e: ChangeEvent<HTMLInputElement>): void => {
      if(e.target.name === "task") {
        setTitle(e.target.value)
      } else {
        setDescription(e.target.value)
      }
    };

    const addTask = (): void => {
      const newTask = { title: title, description: description, }
      setTodoList([...todoList, newTask]);
    } 

  return (
    <div className="App">
      <div className="header">
        <div className="titleContainer">
        <h4>Title:</h4>
        <input type="text" placeholder="Enter tittle" name="task" onChange={handleChange} />
        </div>
        <div className="descriptionContainer">
        <h4>Description:</h4>
        <input type="text" placeholder="Enter description" name="description" onChange={handleChange} />
        </div>
        <button onClick={addTask}>Create</button>
      </div>
      <div className="todoList"></div>
    </div>
  );
}

export default App;
